(window.webpackJsonp=window.webpackJsonp||[]).push([[82],{923:function(t,n,v){"use strict";v.r(n);var _=v(0),a=Object(_.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("h1",{attrs:{id:"🟥-why-yooch-就是希望像素"}},[t._v("🟥 "),n("s",[t._v("Why Yooch")]),t._v(" 就是希望像素！")]),t._v(" "),n("p",[n("s",[t._v('为什么我起这个名字“YOOCH”，其实也没什么其他意义，只不过是我的姓名拼音首字母加了俩"0"，我自己感觉挺好看，同时两个圈无限接近又不相交时看上去就很像无穷大的符号“∞”，这是表示无限大的一种公式，没有具体数字，但是正无穷表示比任何一个数字都大的数值。就好像我想要做的像素画板，我就在今天给他正式命名“Y∞CH”,有着“有界限，无极限”含义，但他还需要一个中文名字，暂时先不起了。这个页面就当是我和“Y∞CH”的一个自我介绍')])]),t._v(" "),n("p",[t._v("原本是对于名字不大上心的，我对于这个点子太过于重视了以至于像是在培养一个孩子，我希望他在众多繁星中熠熠发光，但却忘了怎么给予这个孩子一个真正的灵魂，一个可以让人们尝试去理解与去接触的灵魂，现在我进入心流状态，想到之前很流行的一种文风：")]),t._v(" "),n("pre",[t._v("要说赶路，就不能只说高铁飞机归家忙，\n要说绿皮上的蛇皮袋鼓鼓囊囊，\n要说席地而睡人群熙熙攘攘，\n要说十几个小时的作为硬邦邦，\n要说小小的座椅装不下生活的苦......\n")]),t._v(" "),n("p",[t._v("是啊，这样的文字四方的游子很难不为之动容吧。 我初次见到这段文字时不为所动，因为我就在家里，爸爸辛苦的工作，臭弟弟还要我盯着写作业，还好妈妈也在，就是她的肚子还是会不舒服。她的肚子装的都是委屈，肚皮上有两竖一横的刀疤，两竖是我和弟弟的出生点，一横是取出险些夺走他生命的肿瘤遗留的刀疤。\n我多希望现在是术后的好多年，至少代表肿瘤这么久还没有复发，但是我不是医生，只要妈妈跟我说肚子不舒服我就会明显的听见自己的心跳，这种跳动让我有一丝丝晕眩，就像一年前在市医院里拿到的增强CT的报告单一样。伴随着耳鸣我告诉妈妈：“没事的吧，帮你问问刘博士。”她便不再说，却还是一趟趟的跑厕所。这样的往复，会一遍遍的让我想起确诊的那一年，我很无助，却还是之身带着妈妈化疗做手术，好像我什么都会。去上海做完PATCT后还做了一次化疗但是妈妈的白细胞指标掉的太快只能终止化疗")]),t._v(" "),n("figure",{attrs:{"data-type":"image"}},[n("img",{attrs:{src:"https://pic.imgdb.cn/item/64190f36a682492fcc934611.jpg",alt:"",loading:"lazy"}}),n("figcaption",[t._v("等待穿刺取样的妈妈")])]),t._v(" "),n("p",[t._v("在家里休息了一个礼拜头发开始零零散散的脱落，我知道化疗有副作用的，但我还是心存侥幸。“只上了一次药应该不会掉光的吧！”我这样安慰妈妈，但是她还是准备了一顶假发，紧接着我们就到了市里的中医院开始了化疗。但那之后，头发脱落的越来越快，知道依稀可见她的头皮，妈妈坐在病床上抓着枕头上脱落的头发看着我说头发掉了很多，声波中夹杂着恐惧和尴尬的抖动。随后还和我的好兄弟朱医生一起去买了一顶帽子，最后还是被妈妈嫌弃难看。😣")]),t._v(" "),n("p",[n("i",[t._v("......")]),t._v("\n为什么要说这些呢，其实陪妈妈看病这段时间，只能看得见我在她床前忙前忙后陪她聊天讲经书给她听，还有其他病床的病友一起聊聊天，在医院虽难身体难受但是还是很安慰，身体也就没那么难受了。但是看病不是只有奔波，长期的治疗费用像一块巨石缓缓的压了下来，直到后来不再化疗，还是要周期性的上药，以至于买了老房子还是不能够还清亲戚朋友借来救急的债务。\n没钱治疗，没人照顾，这是只有经历过的人才会理解的绝望。")]),t._v(" "),n("p",[t._v("这个项目也存在与陪母亲求医的过程中，一边在上海的病房里看课程学js，在狭小的酒店里写程序，一边趴在病床上看NCCN，就这样这个项目在我陪伴妈妈治疗的过程中也陪伴着我。直至上周末的开题答辩，老师只有一个问题：“谁会买这个虚拟的像素画呢？”，这个问题确实问住我了，我只能拿国外的例子来辩解，但是我也知道就算换做是我自己也是不会买的，这个画布没有现实中的价值。但是现在我想说它有价值了，我要让他有价值，就像当时陪在母亲身边的我一样，这些个像素也陪着我，现在我要让这些像素陪在需要帮助的人身边。")]),t._v(" "),n("figure",{attrs:{"data-type":"image"}},[n("img",{attrs:{src:"https://pic.imgdb.cn/item/64184f54a682492fcc554599.png",alt:"",loading:"lazy"}}),n("figcaption",[t._v("单人(dao)赴会")])]),t._v(" "),n("pre",[t._v("要说放像素，就不能只说鼠标横点竖点斜着点，\n要说五彩斑斓像彩虹一样围在身旁，\n要说每一次点击都是一点希望，\n要说为了生命绘制最无价的图案......\n")]),t._v(" "),n("p",[t._v("此刻我的情绪零碎，这一路没有流过一次眼泪，我只是往前走，不敢回头看")]),t._v(" "),n("p",[n("name",[t._v("YOOCH")])],1),t._v(" "),n("p",[t._v("今后所有关于"),n("s",[t._v("Y∞CH")]),t._v('希望像素的开发进展都放在"🟧"，等项目基本建成后每一次的更新都放在"🟨"里')])])}),[],!1,null,null,null);n.default=a.exports}}]);